import React from "react";
import { useCart } from "./CartContext";
import { Link } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";

const stripePromise = loadStripe("pk_test_TA_CLE_PUBLIQUE_ICI"); // remplace ici ta cl√© publique

export default function CartPage() {
  const { cart, clearCart } = useCart();

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleCheckout = async () => {
    const stripe = await stripePromise;

    const res = await fetch("http://localhost:4242/create-checkout-session", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ cart }),
    });

    const session = await res.json();
    await stripe.redirectToCheckout({ sessionId: session.id });
  };

  return (
    <div className="nasaflow-wrapper">
      <div className="nasaflow-container">
        <h1 className="nasaflow-title">üõçÔ∏è Votre Panier</h1>
        {cart.length === 0 ? (
          <p>Votre panier est vide.</p>
        ) : (
          <>
            <ul style={{ marginBottom: "1rem" }}>
              {cart.map((item, i) => (
                <li key={i}>
                  {item.name} ‚Äì CHF {item.price.toFixed(2)}
                </li>
              ))}
            </ul>
            <p className="nasaflow-price">Total : CHF {total.toFixed(2)}</p>

            <button className="nasaflow-button" onClick={clearCart}>
              Vider le panier
            </button>
            <br />
            <button
              className="nasaflow-button"
              style={{ marginTop: "1rem" }}
              onClick={handleCheckout}
            >
              Commander avec Stripe üí≥
            </button>
          </>
        )}
        <br />
        <Link
          to="/"
          className="nasaflow-button"
          style={{ display: "inline-block", marginTop: "1.5rem", textAlign: "center" }}
        >
          ‚Üê Retour √† la boutique
        </Link>
      </div>
    </div>
  );
}
