import React from "react";
import { loadStripe } from "@stripe/stripe-js";
import { motion } from "framer-motion";
import "./NasaFlowShop.css";

const stripePromise = loadStripe("pk_test_12345"); // Remplace par ta vraie clé Stripe

export default function NasaFlowShop() {
  const handleBuy = async () => {
    const stripe = await stripePromise;

    const res = await fetch("/api/checkout", {
      method: "POST",
    });

    const session = await res.json();
    await stripe.redirectToCheckout({ sessionId: session.id });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-pink-100 to-black p-8 text-white">
      <div className="max-w-4xl mx-auto">
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-4xl font-bold text-center mb-4"
        >
          NasaFlow – Respirez la performance
        </motion.h1>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.6 }}
          className="grid grid-cols-1 md:grid-cols-2 gap-6 items-center"
        >
          <img
            src="/nasaflow-box.png"
            alt="Boîte NasaFlow"
            className="rounded-2xl shadow-xl"
          />

          <div className="bg-white text-black p-6 rounded-2xl shadow-2xl">
            <h2 className="text-2xl font-bold mb-2">
              Bandelette magnétique NasaFlow
            </h2>
            <p className="mb-4 text-sm text-gray-700">
              Oxygénez vos performances avec notre bandelette nasale
              magnétique. Réutilisable, confortable, invisible. Idéale pour le
              sport et le sommeil.
            </p>
            <p className="text-xl font-semibold mb-4">CHF 19.90</p>
            <button
              className="w-full bg-pink-700 hover:bg-pink-800 text-white py-3 px-4 rounded-lg"
              onClick={handleBuy}
            >
              Acheter maintenant
            </button>
          </div>
        </motion.div>
      </div>
    </div>
  );
}
